package basic.concepts;


public class secondprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    //1.Number type
		byte age = 127;
		short b = 32_767;
		int c = 2_147_483_647;
		long d = 9_223_372_036_854_775_807L;
		
		
		//Float
		float e = 1.123456789f;
		double u = 1.12345678910111245;
		
		//Boolean type
		boolean type = true;
		
		//char type
		char initial = 'S';
		
		//Display output
		System.out.println("BYte representing:" + age);
		System.out.println("short representing:" + b);
		System.out.println("int representing:" + c);
		System.out.println("long representing:" + d);
		System.out.println("float representing:" + e);
		System.out.println("double representing:" + u);
		System.out.println("Boolean representing:" + type);
		System.out.println("BYte representing:" + initial);
	}
		
	}


